const root = $json.root || $json.root_path || $env.TRANSLATION_ROOT || '/Users/ivy/Library/CloudStorage/OneDrive-Personal/Translation Task';
const jobId = $json.job_id || `job_${Date.now()}`;
const reviewDir = `${root}/Translated -EN/_REVIEW/${jobId}`;
const meta = {
  job_id: jobId,
  root_path: root,
  arabic_v1_path: $json.files?.arabic_v1?.path,
  arabic_v2_path: $json.files?.arabic_v2?.path,
  english_v1_path: $json.files?.english_v1?.path,
  review_dir: reviewDir,
};
const metaB64 = Buffer.from(JSON.stringify(meta), 'utf8').toString('base64');
const pythonBin = $env.PYTHON_BIN || 'python3';
const cmd = `${pythonBin} /Users/Code/workflow/translation/scripts/openclaw_translation_orchestrator.py --meta-json-base64 '${metaB64}'`;
const message = [
  'You are translator-main. Run the following command exactly once and do not alter arguments:',
  cmd,
  'After command finishes, respond with one short line: ORCHESTRATOR_DONE',
].join('\n');
const payload = {
  message,
  name: 'translation-revision-job',
  agentId: $env.OPENCLAW_AGENT_ID || 'translator-main',
  sessionKey: `job:${jobId}`,
  wakeMode: 'now',
  deliver: false,
  meta,
  model: $env.OPENCLAW_PRIMARY_MODEL || 'openai-codex/gpt-5.3-codex',
  timeoutSeconds: Number($env.OPENCLAW_HOOK_TIMEOUT_SECONDS || 900),
};
return [{ ...$json, job_id: jobId, meta, hook_payload: payload }];

const all = $input.all().map(i => i.json);
const ctx = all.find(x => x.hook_payload && x.meta) || {};
const candidate = all.find((x) => Object.prototype.hasOwnProperty.call(x, 'runId') || Object.prototype.hasOwnProperty.call(x, 'ok') || Object.prototype.hasOwnProperty.call(x, 'body') || Object.prototype.hasOwnProperty.call(x, 'data')) || {};
const ack = candidate.body || candidate.data || candidate;
const runId = ack.runId || candidate.runId || '';
const ok = ack.ok === true || candidate.ok === true;
const hookAccepted = ok && Boolean(runId);
return [{
  ...ctx,
  hook_ack: ack,
  hook_accepted: hookAccepted,
  openclaw_run_id: runId,
}];

const raw = $json.stdout || '{}';
let parsed;
try { parsed = JSON.parse(raw); } catch (e) {
  parsed = { ok: false, status: 'needs_revision', errors: [`response_parse_error: ${e.message}`] };
}
const src = $items('Code Parse Hook Ack + Context', 0, 0)?.[0]?.json || {};
return [{
  ...src,
  hook_result: parsed,
  openclaw_run_id: src.openclaw_run_id || '',
  hook_result_ok: parsed.ok === true,
}];

const r = $json.hook_result || {};
return [{
  job_id: r.job_id || $json.job_id,
  review_dir: r.review_dir || $json.meta?.review_dir,
  status: r.status || 'review_pending',
  artifacts: r.artifacts || {},
  quality: r.quality || {},
  errors: r.errors || [],
  openclaw_run_id: $json.openclaw_run_id || '',
  callback_base: `${$env.N8N_PUBLIC_BASE_URL || ''}/webhook/review`,
}];

return [{
  ...$json,
  status: 'needs_revision',
  error_message: 'OpenClaw hook request was not accepted',
  openclaw_run_id: $json.openclaw_run_id || '',
}];

return [{
  ...$json,
  status: 'needs_revision',
  error_message: ($json.hook_result?.errors || []).join('; ') || 'OpenClaw hook processing failed',
  openclaw_run_id: $json.openclaw_run_id || '',
}];
